import React, { useState, useEffect } from 'react';
import StatsProduits from './components/StatsProduits';
import { Produit } from './components/StatsProduits'; // Importer le type ici

const App: React.FC = () => {
  const [produits, setProduits] = useState<Produit[]>([]);

  useEffect(() => {
    // Remplace ceci par ton appel API ou tes données statiques
    const fetchedProduits: Produit[] = [
      { id: 1, nom: 'Produit A', categorie: 'Catégorie 1', quantite: 50, seuil: 10 },
      { id: 2, nom: 'Produit B', categorie: 'Catégorie 2', quantite: 30, seuil: 5 },
      { id: 3, nom: 'Produit C', categorie: 'Catégorie 3', quantite: 70, seuil: 20 },
    ];
    setProduits(fetchedProduits);
  }, []);

  return (
    <div>
      <h1>Dashboard</h1>
      <StatsProduits produits={produits} />
    </div>
  );
};

export default App;
