// pages/Categorie.tsx
import React, { useState } from 'react';
import { Container, Table, Button, Form } from 'react-bootstrap';
import { Categorie } from '../types'; // Assurez-vous d'importer l'interface

const CategoriePage: React.FC = () => {
  const [nom, setNom] = useState('');
  const [description, setDescription] = useState('');
  const [categories, setCategories] = useState<Categorie[]>([]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const nouvelleCategorie: Categorie = { nom, description };
    setCategories([...categories, nouvelleCategorie]);
    setNom('');
    setDescription('');
  };

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Gestion des Catégories</h2>

      {/* Formulaire d'ajout de catégorie */}
      <Form onSubmit={handleSubmit} className="mb-4">
        <Form.Group className="mb-3">
          <Form.Label>Nom de la catégorie</Form.Label>
          <Form.Control
            type="text"
            value={nom}
            onChange={(e) => setNom(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Description</Form.Label>
          <Form.Control
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Ajouter
        </Button>
      </Form>

      {/* Liste des catégories */}
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((categorie, index) => (
            <tr key={index}>
              <td>{categorie.nom}</td>
              <td>{categorie.description}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default CategoriePage;
