import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import Header from './components/Header';
import Dashboard from './pages/Dashboard';
import ProduitDetail from './pages/ProduitDetail';
import History from './pages/History';
import CategoriesPage from './pages/CategoriesPage'; // <-- ajouter ici
import ProduitTable from './pages/ProduitTable'

const App: React.FC = () => {
  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/product/:id" element={<ProduitDetail />} />
        <Route path="/history" element={<History />} />
        <Route path="/categories" element={<CategoriesPage />} /> {/* <-- nouvelle route */}
        <Route path="/produitTable" element={<Produits/>} /> {/* <-- nouvelle route */}

      </Routes>
    </Router>
  );
};

export default App;
