import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import StatsProduits from './components/StatsProduits';
import Dashboard from './pages/Dashboard'; // Import du Dashboard
import './App.css';

type Produit = {
  nom: string;
  quantite: number;
  seuil: number;
};

const App: React.FC = () => {
  const [produits, setProduits] = useState<Produit[]>([]);
  const [search, setSearch] = useState<string>('');

  // Fonction pour récupérer les produits depuis le backend (Node.js)
  const fetchProduits = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/produits'); // API backend
      setProduits(response.data);
    } catch (error) {
      console.error('Erreur lors de la récupération des produits:', error);
    }
  };

  // Charger les produits au démarrage de l'application
  useEffect(() => {
    fetchProduits();
  }, []);

  // Fonction pour rechercher un produit
  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value);
  };

  // Filtrer les produits selon la recherche
  const filteredProduits = produits.filter(produit =>
    produit.nom.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      {/* En-tête */}
      <header className="App-header">
        <h1>Gestion des Produits</h1>
      </header>

      <Container className="mt-4">
        <Row>
          <Col md={4}>
            <h2>Tableau de Bord</h2>
            {/* Composant Dashboard */}
            <Dashboard />
          </Col>

          <Col md={8}>
            <h2>Gestion des Stocks</h2>
            {/* Formulaire de recherche */}
            <Form>
              <Form.Group controlId="search">
                <Form.Control
                  type="text"
                  placeholder="Rechercher un produit..."
                  value={search}
                  onChange={handleSearch}
                />
              </Form.Group>
            </Form>

            {/* Affichage des statistiques des produits */}
            <StatsProduits produits={filteredProduits} />

            {/* Liste des produits filtrés */}
            <div className="mt-4">
              <h4>Liste des Produits</h4>
              <ul>
                {filteredProduits.map((produit, index) => (
                  <li key={index}>
                    {produit.nom} (Stock: {produit.quantite}, Seuil: {produit.seuil})
                  </li>
                ))}
              </ul>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default App;
