import axios from 'axios';

type Produit = {
  id: number;
  nom: string;
  categorie: string;
  quantite: number;
  seuil: number;
};

export default function Dashboard() {
  const [products, setProducts] = useState<Produit[]>([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/produits')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
