import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Table } from 'react-bootstrap';
import axios from 'axios';

const History = () => {
  const [historique, setHistorique] = useState<any[]>([]);

  useEffect(() => {
    // Remplacer par l'URL de ton API backend pour récupérer l'historique
    axios.get('/api/history')
      .then(response => setHistorique(response.data))
      .catch(error => console.error('Erreur lors de la récupération de l\'historique des stocks', error));
  }, []);

  return (
    <Container>
      <Row className="my-3">
        <Col>
          <h2>Historique des Stocks</h2>
        </Col>
      </Row>
      <Row>
        <Col>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Produit</th>
                <th>Date</th>
                <th>Type</th>
                <th>Quantité</th>
              </tr>
            </thead>
            <tbody>
              {historique.map((entry, index) => (
                <tr key={index}>
                  <td>{entry.produit}</td>
                  <td>{entry.date}</td>
                  <td>{entry.type}</td>
                  <td>{entry.quantite}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
}

export default History;
