import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from 'react-bootstrap';

// Import des composants et pages
import Header from './components/Header';
import Accueil from './pages/Accueil';
import Historique from './pages/Historique';
import ProduitTable from './pages/ProduitTable';
import CatTable from './pages/CatTable';
import AjoutProduit from './pages/AjoutProduit';
import AjoutCategorie from './pages/AjoutCategorie';

const App: React.FC = () => {
  const [produits, setProduits] = useState<any[]>([]);
  const [categories, setCategories] = useState<any[]>([]);

  const ajouterProduit = (produit: any) => {
    setProduits([...produits, produit]);
  };

  const ajouterCategorie = (categorie: any) => {
    setCategories([...categories, categorie]);
  };

  return (
    <Router>
      <Header />
      <Container className="mt-4">
        <Routes>
          <Route path="/" element={<Accueil ajouterProduit={ajouterProduit} ajouterCategorie={ajouterCategorie} />} />
          <Route path="/historique" element={<Historique />} />
          <Route path="/produits" element={<ProduitTable produits={produits} />} />
          <Route path="/categories" element={<CatTable categories={categories} />} />
        </Routes>
      </Container>
    </Router>
  );
};

export default App;
