import React from 'react';
import { Table, Button } from 'react-bootstrap';

const CatTable: React.FC<{ categories: any[] }> = ({ categories }) => {
  return (
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          <th>Nom</th>
          <th>Description</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {categories.map((cat, index) => (
          <tr key={index}>
            <td>{cat.nom}</td>
            <td>{cat.description}</td>
            <td>
              <Button variant="danger" onClick={() => alert('Supprimer')}>Supprimer</Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default CatTable;
