import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';

const AjoutProduit: React.FC<{ onSubmit: (produit: any) => void }> = ({ onSubmit }) => {
  const [nom, setNom] = useState('');
  const [description, setDescription] = useState('');
  const [uniteMesure, setUniteMesure] = useState('');
  const [categorieId, setCategorieId] = useState<number | null>(null);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const newProduit = { nom, description, unite_mesure: uniteMesure, categorie_id: categorieId };
    onSubmit(newProduit);
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Label>Nom</Form.Label>
        <Form.Control
          type="text"
          value={nom}
          onChange={e => setNom(e.target.value)}
        />
      </Form.Group>

      <Form.Group>
        <Form.Label>Description</Form.Label>
        <Form.Control
          type="text"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
      </Form.Group>

      <Form.Group>
        <Form.Label>Unité de mesure</Form.Label>
        <Form.Control
          type="text"
          value={uniteMesure}
          onChange={e => setUniteMesure(e.target.value)}
        />
      </Form.Group>

      <Form.Group>
        <Form.Label>Catégorie</Form.Label>
        <Form.Control
          type="number"
          value={categorieId ?? ''}
          onChange={e => setCategorieId(Number(e.target.value))}
        />
      </Form.Group>

      <Button variant="primary" type="submit">Ajouter Produit</Button>
    </Form>
  );
};

export default AjoutProduit;
