import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Table, Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // Importation de Link pour la navigation
import axios from 'axios';

const History = () => {
  const [historique, setHistorique] = useState<any[]>([]);

  useEffect(() => {
    // Remplacer par l'URL de ton API backend pour récupérer l'historique
    axios.get('/api/history')
      .then(response => setHistorique(response.data))
      .catch(error => console.error('Erreur lors de la récupération de l\'historique des stocks', error));
  }, []);

  return (
    <Container className="mt-4">
      <Row className="mb-3">
        <Col>
          <h2 className="text-center text-primary">Historique des Stocks</h2>
        </Col>
      </Row>

      <Row>
        <Col>
          <Card className="shadow-lg">
            <Card.Body>
              <Table striped bordered hover responsive>
                <thead className="table-light">
                  <tr>
                    <th>Produit</th>
                    <th>Date</th>
                    <th>Type</th>
                    <th>Quantité</th>
                  </tr>
                </thead>
                <tbody>
                  {historique.length > 0 ? (
                    historique.map((entry, index) => (
                      <tr key={index}>
                        <td>{entry.produit}</td>
                        <td>{entry.date}</td>
                        <td>{entry.type}</td>
                        <td>{entry.quantite}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan={4} className="text-center">Aucune donnée disponible</td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row className="mt-3">
        <Col className="d-flex justify-content-center">
          <Link to="/"> {/* Retour vers la page d'accueil (Dashboard) */}
            <Button variant="secondary">Retour au Tableau de Bord</Button>
          </Link>
        </Col>
      </Row>
    </Container>
  );
}

export default History;
