import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ProduitTable from './pages/ProduitTable'; // Vérifie le bon chemin
import History from './pages/History'; 
import CategoriesPage from './pages/CategoriesPage'; 

// Définir les données simulées pour produits et catégories
const produits = [
  { id: 1, nom: 'Produit 1', description: 'Description 1', categorie_id: 1, unite_mesure: 'kg' },
  { id: 2, nom: 'Produit 2', description: 'Description 2', categorie_id: 2, unite_mesure: 'l' },
];

const categories = [
  { id: 1, categorie_id: 1, nom: 'Catégorie 1', description: 'Description catégorie 1' },
  { id: 2, categorie_id: 2, nom: 'Catégorie 2', description: 'Description catégorie 2' },
];

// Définir une fonction pour supprimer un produit
const onDelete = (id: number) => {
  console.log(`Produit avec id ${id} supprimé`);
};

const App: React.FC = () => {
  return (
    <Router>
      <Routes>
        <Route path="/history" element={<History />} />
        <Route path="/categories" element={<CategoriesPage />} />
        <Route
          path="/produitTable"
          element={<ProduitTable produits={produits} categories={categories} onDelete={onDelete} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
