// pages/Categories.tsx
import React from 'react';
import { Table, Button, Container } from 'react-bootstrap';

export interface Categorie {
  categorie_id?: number;
  nom: string;
  description: string;
}

interface Props {
  categories: Categorie[];
  supprimerCategorie: (categorieId: number) => void;
}

const Categories: React.FC<Props> = ({ categories, supprimerCategorie }) => {
  return (
    <Container className="mt-4">
      <h3>Liste des Cat√©gories</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((categorie) => (
            <tr key={categorie.categorie_id}>
              <td>{categorie.categorie_id}</td>
              <td>{categorie.nom}</td>
              <td>{categorie.description}</td>
              <td>
                <Button
                  variant="danger"
                  onClick={() => supprimerCategorie(categorie.categorie_id!)}
                >
                  Supprimer
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Categories;
